sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '' || oldValue == newValue) {
        return;
    }
    jslog('started on change');
    var getEmailAdd = new GlideAjax("getEmailOfRequestedfor");
    getEmailAdd.addParam("sysparm_name", "Emailofrequestedfor");
    getEmailAdd.addParam("sysparm_userID", g_form.getValue('u_requested_for'));
    jslog("sis id is " + g_form.getValue("u_requested_for"));
    getEmailAdd.getXML(setemail);

    //Type appropriate comment here, and begin script below

    function setemail(response) {
        var emailfromscriptinclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('u_requestedfor_email');
        g_form.setValue('u_requestedfor_email', emailfromscriptinclude);
    }
}
